    /**
     * converts a binary array to a single number
     */
    "bitArrayToInt"(arr) {
        var res = 0;
        for (let i = 0,minLen=Math.min(arr.length, 8*8); i < minLen; i++) {
            if (arr[i])
                res += Math.pow(2, i);
        }
        return res;
    },

    /** @summary capitalize the first character of a given string
        * @param {string} word 
        * @return {string} new processed string
        */
    "firstCharUp"(word){
        return word.charAt(0).toUpperCase() + word.slice(1);
    },

    /**
    * @summary recursively merge two Objects
        * @desc overlay sec onto main. keeps properties of both. undefined properties of main are directly assigned from sec.
        * - ex: main : {a, b:b1} sec:{b:b2, c} -> res:{a , b:b2, c} where:
        * - -res.a===main.a
        * - -res.c===sec.c
        * - -res.b!==sec.b2
        * @param {Object} main 
        * @param {Object} sec 
        */
    "mergeRec":function thisFun(main, sec){
        for(let p in sec) {
            //if(main[p]===undefined) //commented bcs implicitly included in the next if condition //(typeof(undefVar) <=> "undefined")
            //if(deepCopy && typeof(main[p]) =="object") //too big an hustle
            //main[p] = sec[p];
            if (sec[p].constructor == Object && typeof (main[p]) == "object")
                thisFun(main[p], sec[p]);
            else
                main[p] = sec[p];
        }
        return main;
    }.bind(this),

    /** @summary repeatedly tries to fetch all globalNames with global.get(). 
        * @param {function} cb called once ALL globalName are defined
        * @param {Object} opts object holding optional function params. can be omitted passing the first globalName instead.
        * @param {number} opts.timeout ms delay after which stop trying and either call opts.errorCb() or log an error. defaults to 15000
        * @param {number} opts.retryDelay ms between each attempt. defaults to 100
        * @param {function} opts.errorCb function to call after timeout. defaults to falsy, logging a warning
        * @param  {...any} globalNames series of strings
        */
    "callWhenDef":function callWhenDef(cb, opts, ...globalNames){
        if(typeof(opts) == "string")
            globalNames.push(opts);
        let {timeout:timeout=3000, retryDelay:retryDelay=100, errorCb:errorCb=null} = (typeof(opts) == "string" ? opts : {});
        var tout,intv;
        //set timeout unless false or not specified
        if (timeout) {
            tout = setTimeout(() => {
                clearInterval(intv);
                errorCb ? errorCb() : node.warn({ err: "callWhenDef_Timeout", arg: arguments })
            }, timeout);
        }
        //set global-chacking interval
        intv = setInterval(() => {
            for (let name of globalNames)
                if (global.get(name) === undefined || global.get(name) === null)
                    return
            clearTimeout(tout);
            clearInterval(intv);
            cb();
        }, retryDelay)
    },
    /** pseudo random string generator
     */
    "randomHexString4":function(){
        return (((1 + Math.random()) * 0x10000) | 0).toString(16).substring(1); //1.X * 2^16 toString(radix) clipFirst //== 4 characters hex
    },
    /** returns an iterator-maker that uses the keys in orderedKeys to access its parent's properties
         * @param {string[]} orderedKeys
         * @returns {function} f() -> iterator object.
         */
    "makeKeyIterator":function (orderedKeys) {
        return function () {
            //console.log("2# this",this); this === parent object
            let keys = orderedKeys, idx = 0;
            return {
                next:()=>{
                    //console.log(this, idx, orderedKeys);
                    return (idx < keys.length ? { value: this[keys[idx++]], done: false } : { done: true });
                }
            }
        }
    },
    /** attach a general iterator going through all the enumerable obj's key.
        * -Not very memory-performant as a redundant key[] array is stored
        * @param {object} the object to which attach the iterator
        * @returns {function} the attached iterator
        */
    "generateIteratorFor": function (obj) {
        obj[Symbol.iterator] = this.makeKeyIterator(Object.keys(obj));
        return obj[Symbol.iterator];
    },
    "errorLog"(...param){ return this.logError(...param);},
    "logError": function thisFun(nodeRef, err){
        if(arguments.length != thisFun.length) throw new SyntaxError(`expected ${thisFun.length} arguments, got ${arguments.length}`);
        var error = (typeof (err) == "string" ? {err:err} : err);
        nodeRef.error(error);
    },
};


