/** repeatedly tries to fetch all globalNames with global.get(). 
    * @param {function} cb called once ALL globalName are defined
    * @param {Object} opts object holding optional function params. can be omitted passing the first globalName instead.
    * @param {number} opts.timeout ms delay after which stop trying and either call opts.errorCb() or log an error. defaults to 15000
    * @param {number} opts.retryDelay ms between each attempt. defaults to 100
    * @param {function} opts.errorCb function to call after timeout. defaults to falsy, logging a warning
    * @param  {...any} globalNames series of strings
    */
function callWhenDef(cb, opts, ...globalNames){
    if(typeof(opts) == "string")
        globalNames.push(opts);
    let {timeout:timeout=3000, retryDelay:retryDelay=100, errorCb:errorCb=null} = (typeof(opts) == "string" ? opts : {});
    var tout,intv;
    //set timeout unless false or not specified
    if (timeout) {
        tout = setTimeout(() => {
            clearInterval(intv);
            errorCb ? errorCb() : node.warn({ err: "callWhenDef_Timeout", arg: arguments })
        }, timeout);
    }
    //set global-chacking interval
    intv = setInterval(() => {
        for (let name of globalNames)
            if (global.get(name) === undefined || global.get(name) === null)
                return
        clearTimeout(tout);
        clearInterval(intv);
        cb();
    }, retryDelay)
}

