/** @summary converts a single integer number to its binary array rappresentation
 * @param {number} val integer
 * @return {number[]} array of NUMBERS, only 0 and 1
 * @throws TypeError if val is not an integer
 * @throws Error if detects possible infinite loop
 */
function intToBitArray(val) {
    var bitArr = [];

    if (typeof (val) != "number" || val % 1 != 0) throw new TypeError(`[${intToBitArray.name}()]:val must be an integer number`);
    //trova il primo ottetto != 0
    /*var minBytes=4-1; //stops 1 too early
    for(let i=0; i<4 && (val>>(32-8*i))!=0; i++)
        minBytes--;
    node.warn({minBytes:minBytes,for:val})
    */

    let nByte = 0;
    do {
        //copia un bit alla volta * 8bit
        for (let i = 0; i < 8; i++)
            bitArr.push((val >> i) & 0x00000001);
        nByte++;
        //node.warn(nByte);node.warn(val >> 8*nByte)
        if (nByte >= 50) throw new Error("triggered infinite loop guard. possible infinite loop at " + intToBitArray.name);
    } while (val >> 8 * nByte)

    return bitArr;
}

function intArrToBitArray(arr) {
    var ret = []
    for (let val of arr)
        intToBitArray(val).forEach(bit => ret.push(bit))
    return ret;
}






