/** repeatedly tries to fetch all flowNames with flow.get(). 
    * @param {function} cb called once ALL flowName are defined
    * @param {Object} opts object holding optional function params. can be omitted passing the first flowName instead.
    * @param {number} opts.timeout ms delay after which stop trying and either call opts.errorCb() or log an error. defaults to 15000
    * @param {number} opts.retryDelay ms between each attempt. defaults to 100
    * @param {function} opts.errorCb function to call after timeout. defaults to falsy, logging a warning
    * @param  {...any} flowNames series of strings
    */
function callWhenDef(cb, opts, ...flowNames) {
    if (typeof (opts) == "string")
        flowNames.push(opts);
    let { timeout: timeout = 3000, retryDelay: retryDelay = 100, errorCb: errorCb = null } = (typeof (opts) == "string" ? opts : {});
    var tout, intv;
    //set timeout unless false or not specified
    if (timeout) {
        tout = setTimeout(() => {
            clearInterval(intv);
            errorCb ? errorCb() : node.warn({ err: "callWhenDef_Timeout", arg: arguments })
        }, timeout);
    }
    //set flow-chacking interval
    intv = setInterval(() => {
        for (let name of flowNames)
            if (flow.get(name) === undefined || flow.get(name) === null)
                return
        clearTimeout(tout);
        clearInterval(intv);
        cb();
    }, retryDelay)
}

