/* jshint asi: true */

// Expects input msgs with topic set

// cache variable name - needs to be unique for each cache
const cacheVarName = 'msgCache'

// get saved context
let msgCache = context.get(cacheVarName) || {}

// Handle cache control messages
if (msg.hasOwnProperty('cacheControl')) {
    // Replay cache if requested
    if (msg.cacheControl === 'REPLAY') {
        for (let topic in msgCache) {
            // Only send to a single client if we can
            if ( msg.hasOwnProperty('_socketId') ) {
                node.send({
                    "topic": topic, 
                    "payload": msgCache[topic],
                    "_socketId": msg._socketId
                })
            } else {
                node.send({
                    "topic": topic, 
                    "payload": msgCache[topic]
                })
            }
        }
        return null
    } else if (msg.cacheControl === 'CLEAR') {
        // Or clear the cach (no msg sent)
        context.set(cacheVarName, {})
        return null
    } else {
        // or do nothing
        return null
    }
}


// ignore other uibuilder control messages
if ( msg.hasOwnProperty('uibuilderCtrl') ) return null

// Keep the last msg.payload by topic
msgCache[msg.topic] = msg.payload

// save context for next time
context.set(cacheVarName, msgCache)

// Show number of cached msgs in status
node.status({fill:'green',shape:'ring',text:'Cached: ' + Object.keys(msgCache).length})

return msg;

