            allProdExcOutput.payload[k] = state[k];
    }
    node.send([null, null, allProdExcOutput,allStatusOutput])
    */
}
//emulate cycle state changes
else if(msg.action=="start"){
    if(state.prod_cycleRun)
        node.error("received start when already running");
    //set state
    state.prod_cycleRun=true;
    state.prod_cycleNumber++;
    state.prod_id = state.exc_id;
    state.exc_status = 2;
    state.prod_quantProd = 2;
    state.exc_quantProd += state.prod_quantProd;
    //emulate completion routine
        //msg.eta = completion delay ms
        //default delay = 5000ms
    setTimeout((state)=>{
        node.warn("cycleOff");
        state.prod_cycleRun=false;

        //state.prod_id = "";
        let esito = Math.random() < 0.8;
        if(esito){
            state.exc_status = 4; // 'C'
        }
        else{
            state.exc_status = 3; // 'P'
            state.exc_quantProd -= state.prod_quantProd;
        }
        node.warn(esito);
        let request={};
        request.payload = new Array();
        request.variable = new Array();
        //create write request for this node //avoid async shenanigans from context.set()
        for(let k in state){
            request.payload.push(state[k]);
            request.variable.push(k);
        }
        request.action="writeAll";
        /*
        var allProdExc={};
        for (let k in state) {
            if (!/^status_/.test(k))
                allProdExc[k] = state[k];
        }
        */
        node.send([{topic:"allProdExc",payload:state},{topic:"cycleOff",payload:state},request])
    },msg.eta ?? 5000,state);
    /*
    var allProdExc = {};
    for (let k in state) {
        if (!/^status_/.test(k))
            allProdExc[k] = state[k];
    }*/
    node.warn("cycleOn");
    node.send([{topic:"allProdExc",payload:state}, null, null]);
}
//read all //outputs an obj
else if(!msg.action || msg.action=="readAll"){
    let allStatusOutput = { payload:{} };
    let allProdExcOutput = { payload: {} };

    /*for(let k in state){
        if(/^status_/.test(k))
            allStatusOutput.payload[k]=state[k];
        else
            allProdExcOutput.payload[k] = state[k];
    }*/

    msg.payload=state;
    delete msg.action;

    node.warn("readAll");
    node.send([msg,null,null]);
}
else if (msg.action == "writeAllStatus") {
    let status=context.get("status");
    if (msg.variable.length) {
        //node.warn("writeAllStatus");
        for (let i = 0; i < msg.variable.length; i++) {
            node.warn({ write: msg.payload[i], to: msg.variable[i] });
            status[msg.variable[i]] = msg.payload[i];
        }
    }
    context.set("status",status);
    node.warn({ statusGoneTo: status });
}
//else write what's in variable
else{

    if (msg.variable.length) {
        //node.warn("writeAll");
        for (let i=0;i<msg.variable.length;i++) {
            node.warn({write:msg.payload[i],to:msg.variable[i]});
            state[msg.variable[i]] = msg.payload[i];
        }
    }
    node.warn({stateGoneTo:state});
}


context.set("state",state);

initialize:
// Code added here will be run once
// whenever the node is started.


context.set("status", {
    status_alife: false,
    status_cycleActive: false,
    status_emergency: false,
    status_idraulicCmd: false,
    status_idPressa: 1234,
    status_pressPressata: 0,
    status_tempPianoSup: 0,
    status_tempPianoInf: 0
});


//generate status changes
let id = setInterval(() => {
    let last = context.get("state") ?? context.get("status");
    if (!last)
        node.error("context undefined");

    let status = {
        "status_alife": !last.status_alife,
        "status_cycleActive": false,
        "status_emergency": last.status_emergency ? false : Math.random() < 0.2,
        "status_idraulicCmd": last.status_idraulicCmd ? false : Math.random() < 0.5,
        "status_idPressa": 1234,
        "status_pressPressata": Math.random() * 350,
        "status_tempPianoSup": 0, //Math.random() * (2*this.status_pressPressata),
        "status_tempPianoInf": 0 //this.status_tempPianoSup - (1 / 3) * this.status_tempPianoInf + Math.random() * (2 / 3) * this.status_tempPianoInf
    }
    status.status_tempPianoSup = Math.random() * (2 * status.status_pressPressata);
    status.status_tempPianoInf = status.status_tempPianoSup - (1 / 3) * status.status_tempPianoSup + Math.random() * (2 / 3) * status.status_tempPianoSup;

    //create write request for this node
    let request = {};
    request.payload = new Array();
    request.variable = new Array();
    let keys = Object.keys(status);
    //node.warn({"req keys":keys});
    for (let k of keys) {
        //node.warn({s:status,"k":k,item:status[k]});
        request.payload.push(status[k]);
        request.variable.push(k);
    }
    request.action="writeAllStatus";
    node.send([null, null, request,{payload:status}])

}, 5000);

context.set("intervalId", id); //cleared in "On stop"

