/**
 * loops value around min and max. (14, 0, 10) -> 4 ,(-2,0,10) -> 8
 * @param {number} val
 * @param {number} min
 * @param {number} max
 */
function wrap(val, min, max) {
    if (val < min)
        val = max - (Math.abs(min - val) % Math.abs(max - min));
    else if (val > max)
        val = min + (Math.abs(val - max) % Math.abs(max - min));
    return val;
}

