/* interfaccia per la lettura ed il controllo delle colonnine 
    esempio:    towCtrl.FA423.greenLight = truthy/falsy;    //will get/set the correspondig bit in towersBitArray
                towCtrl.FA423.hasDevice("greenLight") <=> towCtrl.macHasDevice("FA423","greenLight")  //true if dev has a correspondig bit in posOf[]

    errori:     towCtrl.FA423.unregDev = 5 -> setter does nothing, getter evaluates to null, both log warning  //unregDev === present in dictionary.devKeys but missing in posOf[thatMacKey]
                towCtrl.unknwMac -> Exception    
                towCtrl.FA423.unknDev -> Exception

    template finale:
    {
        "dictionary":{
            "macKeys" : macKey[]    //{string[]} <=> enum
            "devices" : device[]    //{string[]} <=> enum
        },

        "posOf":{
            {macKey}:{  //all dictionary.macKeys are defined and objects
                {device}:0 < number < 2^n | null //bit index //all dictionary.devices are defined
            }
        },

        "sharedBits":{
                {bit}:{ //only the shared ones are defined
                    'devKey': <devKey> //associated device. //it presumes all machines sharing the bit use the same device
                    'output'
                }
           }

        "towersBitArray":Array(NBITS).fill(0),

        "macHasDevice": function (macKey, devKey) { return this.posOf[macKey][devKey] !== null }

        /** all dictionary.macKeys are defined. //dictionary.devices are reference to towersBitArray or non-enumerable errorLogging getters/setters * /
        {macKey}:{  //all dictionary's macKeys are defined. 
            'hasDevice'(...): this.macHasDevice.bind(def, <macKey>, ...)    //alias with one less parameter //not enum
            {linkedDeviceKey}: truthy|falsy,     //only posOf[<macKey>]'s devKeys are defined. //dictionary's devKeys missing in posOf[<macKey>] are instead not-enumerable warning-loggers
            {linkedDeviceKey}: truthy|falsy,
            ..
        },
        * / 
    }
*/
callWhenDef( ()=>{
    const utils = global.get("utils");

    /** error logging function */
    function onUnconfigKeySet(key, macKey) {
        node.warn({
            err: "[towersControl] trying to set() an unconfigured bit.",
            key: key,
            macKey: macKey
        });
    }
    function onUnconfigKeyGet(key, macKey) {
        node.warn({
            err: "[towersControl] trying to get() an unconfigured bit.",
            key: key,
            macKey: macKey
        });
    }
    //also used for initial validation
    const dictionary = {
        /** {String[]} macKeys "machine keys". match the ones used in uibuilder's app. */
        macKeys: ["FA419", "FA420", "FA421", "FA422", "FA423", "MO41", "MO42", "OMET"],
        /**  {String[]} devices devices that may be associated with each towersBitArray bit*/
        devices: ["redLight", "greenLight", "horn"],
    };
    /** index of various devices on towersBitArray.
     * NOT ALL ARE NECESSARILY PRESENT! use macHasDevice() and def[macKey][device]=val instead
     * setting ===null is the same as leaving it undefined, but will throw a warning */
    const posOf = {
        "FA423": {
            "redLight": 0,
            "greenLight": 1,
            "horn": 2
        },
        "FA420": {
            "redLight": 3,
            "greenLight": 4,
            "horn": 5
        },
        "FA421": {
            "redLight": 6,
            "greenLight": 7,
            //set "horn"(val) { onUnconfigKey("horn","FA419") }
            "horn": 5, //same as undefined
        },
        "FA419": {
            "redLight": 8,
            "greenLight": 9,
            "horn": 5
        },
        "OMET": {
            "redLight": 10,
            "greenLight": 11,
            "horn": 2,
        },
        "FA422": {
            "redLight": 12,
            "greenLight": 13,
            "horn": 2,
        },
        "MO41": {
        },
        "MO42": {
        }
    };
    const NBITS = 16; //length of towersBitArray
    //complete posOf with iterators
    posOf[Symbol.iterator] = utils.makeKeyIterator(dictionary.macKeys);
    // posOf[Symbol.iterator] = utils.makeKeyIterator(Object.keys(posOf));
    // posOf.forEach(mac => utils.generateIteratorFor(mac) )
    // const sharedDevs = (()=>{ var arr=new Array(NBITS);  posOf.forEach((v,i) => arr[]) return arr;})()

    //validate and guaratee posOf syntax
        //posOf keys are macKeys
        //posOf[macKey] keys are devices
        //posOf[macKey][devKey] are positive integers < NBITS
    for (let macKey in posOf) {
        if (!dictionary.macKeys.includes(macKey))
            node.warn("[towersControl] Syntax Validation Error: unknown macKey " + macKey + " in posOf{}. typo?");
        for (let linkedDeviceKey in posOf[macKey]) {
            if (!dictionary.devices.includes(linkedDeviceKey))
                node.warn("[towersControl] Syntax Validation Error: unknown linkedDeviceKey " + linkedDeviceKey + " in posOf["+macKey+"]{}. typo?");
            if (posOf[macKey]<0 || posOf[macKey]>=NBITS)
                node.warn("[towersControl] Syntax Validation Error: invalid bit number. must be >= 0 && < " + NBITS + ". in posOf[" + macKey + "]["+linkedDeviceKey+"]. typo?");
        }
    }   //all macKeys have an EXPLICIT posOf[macKey][device] set (!== undefined), set to null and warn otherwise
    //all dictionary macKeys are defined
    for (let macKey of dictionary.macKeys)
        if (posOf[macKey] == undefined || typeof (posOf[macKey]) != "object")
            node.warn("[towersControl] Syntax Validation Error: missing macKey in posOf :" + macKey);
    //all present devices are null || number 
    if (!Object.keys(posOf).every(macKey => dictionary.devices.every(device => posOf[macKey][device] !== undefined))) {
        node.warn("[towersControl]: Syntax validation Warning: implicit null devices in posOf");
        for (let macKey of dictionary.macKeys)
            for (let device of dictionary.devices)
                posOf[macKey][device] = posOf[macKey][device] ?? null;
    }


    {   //generate sharedBits

        const inversePosOf = (() => {
            var arr = new Array(NBITS);
            for(let macKey in posOf)
                for(let devKey in posOf[macKey]){
                    let bit = posOf[macKey][devKey];
                    if(bit === null) continue;
                    arr[bit] ?? (arr[bit] = []);
                    arr[bit].push({ "macKey": macKey, "devKey": devKey });
                }
            return arr;
        })()
        //node.warn({"inversePosOf":inversePosOf});

        var sharedBits = {};
        //define how bits are combined toghether when they're read as one
        let atLeastOneTrue = function(){
            for(let macKey in this.values)
                if(this.values[macKey])
                    return true;
            return false
        }
        //create sharedBits only copying the values with more than one associated machine
        for(let bit in inversePosOf){
            if(inversePosOf[bit].length > 1){
                //check that all machine have the same device on this bit, log a warning if they don't //use the first device anyway
                if(!inversePosOf[bit].every((pair,i,arr) => i==arr.length-1 || pair.devKey== arr[i+1].devKey ))    node.warn({err:"[towerControl|sharedBits{}]: different devices appear to share the same bit. ", bit:bit, revPosOf:inversePosOf[bit]});
                inversePosOf[bit].forEach( pair => {
                    sharedBits[bit] ?? (sharedBits[bit] = { devKey: pair.devKey, values: {}, output: atLeastOneTrue })
                    sharedBits[bit].values[pair.macKey] = false;
                })
            }
        }
        //add iterators
            //for sharedBits
            //for all sharedBits[].values
        let valKeys;
        utils.generateIteratorFor(sharedBits);
        // @ts-ignore
        for(let item of sharedBits){
            valKeys = []; for (let mk in item.values) valKeys.push(mk);
            item.values[Symbol.iterator] = utils.makeKeyIterator(valKeys);
        }
    }
    //node.warn({"sharedBits":sharedBits });
    // sharedBits.forEach( bitItem => bitItem.values.fill(true) );
    // @ts-ignore
    //for(let bitItem of sharedBits) for(let mk in bitItem.values) bitItem.values[mk] = true;



    //towersControl
    var def = {
        /** holds allowed keys as <string>[] //also used for validation purposes on towersControl definition
             * - {String[]} macKeys "machine keys". match the ones used in uibuilder's app.
             * - {String[]} devices devices that may be associated with each towersBitArray bit */
        "dictionary": dictionary,

        /** pairs devices with the corresponding bit index to use in the modbus  queries
            example: posOf[macKey][device]:number    //undefined macKey -> exception like 'undefined reading undefined' //undefined device -> exception like 'undefined reading <device>'
            exposed but not expected to be used.
            "posOf":{
                {macKey}:{  //all dictionary.macKeys are defined and objects
                    {device}:0 < number < 2^n | errorLoggingGetter() //bit index //all dictionary.devices are defined or errorLogging
                }
            },*/
        "posOf": posOf,

        /** 

          "sharedBits":{
                {bit}:{ //only the shared ones are defined
                    'devKey': <devKey> //associated device. //it presumes all machines sharing the bit use the same device
                    'output'
                }
           }*/
        "sharedBits": sharedBits,

        /** array of bits, represented by Numbers that are either 0 or 1
             * - values should be set through towersControl["FA22"].redLight = truthy|falsy
             * - has a lenght of NBITS */
        "towersBitArray": new Array(16).fill(0),

        /** @returns Bool
         * @throws TypeError if macKey or devKey are not in dictionary */
        "macHasDevice": function thisFun(macKey, devKey) {
            if (arguments.length != thisFun.length) throw new SyntaxError(`[towersControl:macHasDevice()] expected ${thisFun.length} arguments, got ${arguments.length}`);
            if (!posOf[macKey] || posOf[macKey][devKey] === undefined) throw new TypeError("[towersControl:macHasDevice()] uregistered macKey or devKey:" + macKey + ", " + devKey);

            return this.posOf[macKey] && typeof (this.posOf[macKey][devKey]) == "number"
            //return this[macKey] && this[macKey].propertyIsEnumerable(devKey)
        },
        /*  
        {macKey}:{  //all dictionary's macKeys are defined. 
            'hasDevice'(...): this.macHasDevice.bind(def, <macKey>, ...)    //alias with one less parameter //not enum
            {linkedDeviceKey}: truthy|falsy,     //only posOf[<macKey>]'s devKeys are defined. //dictionary's devKeys missing in posOf[<macKey>] are instead not-enumerable warning-loggers
            {linkedDeviceKey}: truthy|falsy,
            ..
        },
        ..
        */
        [Symbol.iterator]: utils.makeKeyIterator(dictionary.macKeys)
    }



    //complete towersControl
        //adds a setter and getter for EACH macKey and linkedDeviceKey, even if they are not registered in posOf
        //they will modify the corresponding towersBitArray's pos instead of themselves.
        //setters convert the value to either 0 or 1. //not true/false for easier math and conversions
        //setters&getters paired with unconfigured devices (posOf[][]===null) will be NOT ENUMERABLE and log a warning
        //adds a NOT ENUMERABLE hasDevice() function, which aliases macHasDevice(macKey,...)
    {
        const devIsRegistered = function (macKey, linkedDeviceKey) {
            return posOf[macKey][linkedDeviceKey] !== null && posOf[macKey][linkedDeviceKey] !== undefined
        }
        const devIsShared = function (macKey, linkedDeviceKey){
            return devIsRegistered(macKey,linkedDeviceKey) && sharedBits[posOf[macKey][linkedDeviceKey]]
        }
        for (let macKey of dictionary.macKeys) {
            def[macKey] = {};
            Object.defineProperty(def[macKey], "hasDevice", {
                value: def.macHasDevice.bind(def, macKey)
            });
            for (let linkedDeviceKey of dictionary.devices) {
                //node.warn("setting "+macKey+ " "+linkedDeviceKey)
                //if device is not configured add an error-logging getter/setter
                if (true || devIsRegistered) {
                    Object.defineProperty(def[macKey], linkedDeviceKey, {
                        //if bit is registered aka (typeof posof[][] == "number") <=> !== null && >=0
                        set(val) {
                            let idx = def.posOf[macKey][linkedDeviceKey];
                            if (devIsRegistered(macKey, linkedDeviceKey)){
                                //if bit/device is shared by multiple machines
                                if(sharedBits[idx]){
                                    sharedBits[idx].values[macKey] = (val ? true : false) //store the state for that machine
                                    def.towersBitArray[idx] = sharedBits[idx].output(); //set using the output function defined for that device.
                                }
                                else
                                    def.towersBitArray[idx] = (val ? 1 : 0);
                            }
                            else
                                onUnconfigKeySet(macKey, linkedDeviceKey);
                        },
                        get() {
                            let idx = def.posOf[macKey][linkedDeviceKey];
                            if (idx !== null && idx !== undefined)
                                return def.towersBitArray[def.posOf[macKey][linkedDeviceKey]]
                            else {
                                onUnconfigKeyGet(macKey, linkedDeviceKey);
                                return undefined;
                            }
                        },
                        enumerable: def.macHasDevice(macKey, linkedDeviceKey)
                    })
                }
                else {
                    Object.defineProperty(def[macKey], linkedDeviceKey, {
                        set(val) {
                            onUnconfigKeySet(macKey, linkedDeviceKey);
                        },
                        get() {
                            onUnconfigKeyGet(macKey, linkedDeviceKey);
                            return undefined;
                        },
                        enumerable: false
                    })
                }
            }
        }
    }

    //validate towersBitArray syntax

    /*
    //complete posOf
        //attach to each null key an error-logging function
        //--EDIT--allow for posOf calls on missing one
    for (let macKey in posOf) {
        for (let linkedDeviceKey in posOf[macKey]) {
            if (posOf[macKey][linkedDeviceKey] === null) {
                Object.defineProperty(posOf[macKey], linkedDeviceKey, {
                    set(val) {
                        onUnconfigKeySet(linkedDeviceKey, macKey)
                    },
                    get() {
                        onUnconfigKeyGet(linkedDeviceKey, macKey);
                        return null;
                    }
                });
            }
        }
    }
    */

    global.set("towersControl", def);
    global.set("towersBitArray", global.get("towersControl").towersBitArray)
    node.send(def);
    node.status({ fill: "green", text: Object.keys(def).join(' & ') });

