            return
        }
    cb();
}

class toutItem {
    #startTime
    #endTime
    //remainingMs
    #cb
    #ref

    constructor(cb,delay){
        this.#startTime = Date.now();
        this.#endTime = this.startTime + delay;
        this.#cb = cb;
        this.#ref = setTimeout(cb,delay);
    }

    get remainingMs(){ return this.#startTime - this.#endTime }
    get startTime() { return this.#startTime }
    get endTime() { return this.#startTime }
    get cb(){ return this.#cb }
    get ref(){ return this.#ref }
}

class SignalCell {
    signalKey;
    programmedStateChanges;
    timerStart;
    timerEnd;

    constructor(cfg={}){
        this.signalKey = "noop"
        this.programmedStateChanges = [];
        this.timerEnd=0;
        this.timerStart=0;

        this.config = cfg;
        this.cfg = this.config;
    }

    programStateChange(toKey, delay, nodeRef){
        this.programmedStateChanges.push({
            toSignalKey:toKey,
            tout:new toutItem(()=>{
                this.signalKey = toKey;

            },delay)
        })
    }

    setTout( toKey, delay){
        if(this.programmedStateChanges[toKey])
            this.programmedStateChanges[toKey].push(setTimeout(()=>{}))
    }

}

