function getView(viewKey){
    let config=global.get("config");
    var vk = viewKey;
    var loopGuardIdx = 99;
    do{
        if(!config.views[vk])
            throw new TypeError("unknown viewKey  " + vk);
        if(typeof(config.views[vk]) == "string"){
            vk = config.views[vk];
        }
        else
            return config.views[viewKey];
    }while(loopGuardIdx--)

    throw new Error("[getView()] retrieving view "+viewKey+" resulted in an infinite loop.");
}

function setViewForIp(viewKey, ip){
    let config = global.get("config");   

    //remove ip from all views.exactIpList (there should only be one)
    for(let vk in config.views){
        let view=config.views[vk];
