//in payload si trovano nome,tipo e commento di n COPPIE di celle, o %MW.
//  all' indice i corrisponder la cella 4400+2*i. la variabile potrebbe per essere di un solo byte.
//in msg.buffer si trova il buffer di byte letto //class:  https://nodejs.org/api/buffer.html

//byte order: da 2301 a 3210 //il plc salva le singole coppie di byte in LittleEndian, mentre la coppia di coppie come BigEndian. (originale: 0123 -> 01,23 -> 23,01 :inMemoria)
for (let i = 0, tmp, buf = msg.buffer; i < buf.length - 1; i += 2) {
    tmp = buf[i];
    buf[i] = buf[i + 1];
    buf[i + 1] = tmp;
}
//usa la
var err;
for (let i = 0; msg.payload[i] !== undefined; i++) {

    msg.payload[i].address = msg.addressOffset + 2 * i;


    if (msg.buffer[2 * i] === undefined || msg.buffer[2 * i + 1] === undefined || msg.buffer[2 * i] === null || msg.buffer[2 * i + 1] === null) {
        if(!err) err={};
        if (err.missingDataException === undefined)
            err.missingDataException = {};
        err.missingDataException["at_idx_" + i] = msg.buffer;
        msg.payload.value = "missing";
        msg.payload.rawValue = "missing";
    }
    else{
        msg.payload[i].rawValue = "0x" + msg.buffer.readUint32LE(4 * i).toString(16);
        switch (msg.payload[i].type.toUpperCase()) {
            case "UINT": {
                msg.payload[i].value = msg.buffer.readUint16LE(4*i);
                break;
            }
            case "UDINT": {
                msg.payload[i].value = msg.buffer.readUint32LE(4 * i);
                break;
            }
            case "INT": {
                msg.payload[i].value = msg.buffer.readInt16LE(4 * i);
                break;
            }
            case "DINT": {
                msg.payload[i].value = msg.buffer.readInt32LE(4 * i);
                break;
            }
            case "REAL": {
                msg.payload[i].value = msg.buffer.readFloatLE(4*i).toPrecision(7);
                break;
            }
            default: {
                if (!err) err = {};
                if(err.unknownTyperException===undefined)
                    err.unknownTypeException= {};
                err.unknownTypeException["at_idx_" + i] = msg.payload[i].type;

                msg.payload[i].value = "unkType";
            }
        }

    }
}
if(err)
    node.warn(err);
return msg;

