if (msg.payload.length != lastData.length)
    node.warn("length mismatch between data: " + msg.payload.length + "!="+ lastData.length);


let changed={length:0};

for(let i=0; i<msg.payload.length && i<lastData.length; i++){
    let newD = msg.payload[i];
    let lastD = lastData[i];

    if( newD.value != lastD.value
    || newD.rawValue != lastD.rawValue){
        changed[newD.name]={add:4400,from:newD.value,to:lastD.value,raws:newD.rawValue+" -> "+lastD.rawValue}
        changed.length++;
    }
}

if(changed.length){
    node.warn(changed);
    return msg;
