function callWhenDef(cb, opts, ...globalNames) {
    if (typeof (opts) == "string")
        globalNames.push(opts);
    let { timeout: timeout = 3000, retryDelay: retryDelay = 100, errorCb: errorCb = null } = (typeof (opts) == "string" ? opts : {});
    var tout, intv;
    //set timeout unless false or not specified
    if (timeout) {
        tout = setTimeout(() => {
            clearInterval(intv);
            errorCb ? errorCb() : node.warn({ err: "callWhenDef_Timeout", arg: arguments })
        }, timeout);
    }
    //set global-chacking interval
    intv = setInterval(() => {
        for (let name of globalNames)
            if (global.get(name) === undefined || global.get(name) === null)
                return
        clearTimeout(tout);
        clearInterval(intv);
        cb();
    }, retryDelay)
}

