            },
            forIp(ip) { return this[this.keyForIp(ip)]; }, //not enumerable

            setViewForIp(viewOrViewKey, ip) {
                let newView = (typeof viewOrViewKey == "string" ? this.views[viewOrViewKey] : viewOrViewKey);

                for (let view of this.views)
