    //or define enabledTabs as the complementary set of disabledTabs in uiAbs's tabs
    for (let view of uiAbs.views) {
        let [missingPropName, presentPropName] = (view.enabledTabs ? ["disabledTabs", "enabledTabs"] : ["enabledTabs", "disabledTabs"])
        if (!view[missingPropName]) { //makes sure it is indeed missing //needed because default is a reference to another view.
            //adds an enumerable missingPropName get/set and a writable, not enumerable missingPropName+_cache
            utils.defineCachedProp(view, missingPropName,
                thisArg => (uiAbs.dictionary.tabs).filter(v => !thisArg[presentPropName].includes(v))
            )
        }
