        }
        //allIp.every( (ip,i) => allIp.slice(i).every( seqIp => ip != seqIp ))

        //view.home is falsy or a tab (this.tree key)
        for(let view of uiAbs.views)
            if(!uiAbs.tree[view.home])
                node.error(new SyntaxError(`uiAbs() SyntaxError: the home for the view ${vk} is not a tab. home: ${view.home} AbstractTabs: ${uiAbs.dictionary.tabs}`));

    //check tree's syntax
        //all


    class UiSession {
        //always present
        id; //any
        socketId;
        clientIp;
        created;    //number //unixTime
        //undefined untill a view is explicitly set, or inited through constructor
        currentView;    //string
        currentViewKey; //string
        //undefined until used by expiration-related methods
        lastActive;     //number
        expirationTout; //tout id

        constructor(id, {clientIp, socketId}, { view = null, viewKey = null, ip=arguments[1].clientIp }, expireAfterMsFromNow = "never") {
            if (arguments.length < 1 || arguments.length > 4) throw new SyntaxError("new UiSession() arguments count mismatch! (" + arguments.length + ")")
            this.id = id;
            this.clientIp = clientIp;
            this.socketId = socketId;
            this.created = Date.now();
            this.initTree();

            //'optionaly' set this.currentView and currentViewKey
            if (view || viewKey || clientIp) {
                //extrapolate view and viewKey from 2nd argument
                //at least (and at most) one must be defined //nothing breaks otherwise, but you might forget to enforce a view
                //precedence as source is view -> viewKey -> ip
                let uiAbs = global.get("uiAbs");
                this.currentView = view || (viewKey ? uiAbs.views[viewKey] : uiAbs.views.forIp(ip))
                this.currentViewKey = viewKey || (ip ? uiAbs.views.keyForIp(ip) : (() => { //parse uiAbs.views looking for ===
                    for (let vk in uiAbs.views)
                        if (uiAbs.views[vk] === view)
                            return vk;
                    node.error(new Error("new UiSession() no uiAbs.views[v] === view"));
                })())
            }
            else {
                this.setDefaultState();
                node.warn("UiSession() created a UiSession without a view");
            }
            //'optional' like setting a view
            //if the server is never closed and you use the socketId as id the connections will pile up
            if (typeof expireAfterMsFromNow != "string")
                this.setExpiration(expireAfterMsFromNow);

        }

        initTree() {
            let utils = global.get("utils");
            let uiAbs = global.get("uiAbs");

            this.tree = utils.mergeRec({}, uiAbs.tree);
            this.tree[Symbol.iterator] = uiAbs.tree[Symbol.iterator];
        }

        setDefaultState() {
            let utils = global.get("utils");
            let uiAbs = global.get("uiAbs");

            for (let tab of uiAbs.tree) {
                this.tree.isVisible = tab.isVisible;
                this.tree.isEnabled = tab.isEnabled;
            }
        }

        refreshExpiration() {
            this.lastActive = Date.now();
        }

        setExpiration(msFromNow) {
            this.expirationTout = setTimeout(() => {
                let diff = (this.lastActive + msFromNow) - Date.now();
                if (diff > 0)   //set a new timeout to cover the remaining time
                    this.setExpiration(diff);
                else    //remove this session from uiAbs
                    global.get("uiAbs").deleteUiSession(this);
                node.warn({txt:"expired",del:msFromNow,t: this, lA: this.lastActive, lAstr:new Date().toTimeString(),diff:diff })
            }, msFromNow)
            this.lastActive = Date.now();
            //node.warn({txt:"expiration set",del:msFromNow, t: this, lA: this.lastActive, lAstr: new Date().toTimeString() })
        }


    }



    global.set("uiAbs", uiAbs);
    node.status({ fill: "green", text: Object.keys(uiAbs).join(' & ') });
    return uiAbs;
}, "utils","config");

