            window.injectedLabelStyle ?? window.injectedLabelStyle = document.head.inserAdjacentHTML("beforeend",\`
                <style>
                    .injected-label {
                        position:absolute;
                        top:calc(0 - 1em - 3px);
                        width:fit-content;
                    }
                </style>
            \`)

            const injectLabelElement = target => {
                //attach class to target
                target.classList.includes("injected-label-target") || target.classList.add("injected-label-target");
                //prepend label element to target
                if(!target.previousElementSibling.classList.includes(${labelClass}))
                    return target.inserAdjacentHTML("beforebegin",
                        "<div "+${extraAttributes}+'class="${labelClass} injected-label">'+${label}+'</div>'
                    )
                return target.previousElementSibling;
            } 

            //attach to specific group
            if( ${groupClass} && ${insertBeforeClass}){
                //assume group with that class exists
                let group = document.getElementsByClassName(${groupClass})[${groupIndex}];
                if(group.getElementsByClassName(${labelClass}).length > 0){
                    let el = group.getElementsByClassName(${insertBeforeClass})[0];
                    injectLabelElement(el);
                }
            }
            //attach to all groups simultaneously
            else if(${insertBeforeClass}){
                //get all targets
                let els = document.getElementsByClassName(${insertBeforeClass});
                els.forEach( el => injectLabelElement(el) )
            }
        </script>
    `

}

