class DefaultedMap extends Map {
    defaultValue
    defaultKey

    constructor(defaultValue, ...args) {
        super(...args);
        this.defaultValue = defaultValue;
        this.defaultKey = "default";
        super.set(this.defaultKey, this.defaultValue)
    }

    get(key) {
        return super.get(key) ?? this.defaultValue;
    }

    setDefault(val) {
        this.defaultValue = val;
    }

    setDefaultKey(key) {
        this.defaultKey = key;
    }

}

function msTo(val, unit, toFixedDecimals = undefined) {
    let units = ["ms", "s", "min", "h", "gg"];
    let unitFactors = [1, 1000, 60, 60, 24]; //in terms of the preceding unit //ex. min are 1*1000*60
    let factor = 1;
    for (let i = units.indexOf(unit); i >= 0; i--)
        factor *= unitFactors[i];
    const toFixedAsNum = (val, p) => { let pow = Math.pow(10, p); return Math.round(val * pow) / pow }
    return (toFixedDecimals === undefined) ? val / factor : toFixedAsNum(val / factor, toFixedDecimals);
}


function toMs(val, unit) {
    let units = ["ms", "s", "min", "h", "gg"];
    let unitFactors = [1, 1000, 60, 60, 24]; //in terms of the preceding unit //ex. min are 1*1000*60
    let factor = 1;
    for (let i = units.indexOf(unit); i >= 0; i--)
        factor *= unitFactors[i];
    //node.warn({txt:"[toMs]",res:val*factor,from:arguments})
    return Math.round(val * factor);
}


/**
 * recursively traverse views.
 * @returns the view at the end of the reference chain or undefined
 */
function getView(viewKey, haystack) {
    if (typeof haystack[viewKey] == "string") {
        //recurse over a smaller set
        let slicedSet = Object.assign({}, haystack);
        delete slicedSet[viewKey];
        return getView(haystack[viewKey], slicedSet)
    }
    else {
        if (haystack[viewKey] === undefined)
            node.warn("[fallbackGUIVars] getView(" + viewKey + ") " + (Object.keys(haystack).length ? "is undefined" : " is part of a self-referencing loop"));
        return haystack[viewKey]; //self referencing props result in 'undefined'
    }
}

function withoutDuplicates(array, isDup = (a, b) => a === b){
    var shift = 0;
    array.sort().forEach((item, i) => {
        if (i < array.length - 1 && isDup(item, array[i + 1]))
            shift++; //continue
        else if (shift)
            array[i - shift] = item
    })
    array.length -= shift;
    return array;
}
