        const tabUtils={
            /**
             * returns the index of the row with rowId==rowId in the provided table, -1 if not found
             * 
             * @param {number} rowId name of the button to affect, like "sendToPressa1"
             * @param {object} tableRef reference to the row to check, must have a fieldName button.
             * @return {number} the index of the row with rowId==rowId in the provided table, -1 if not found
            */
            posOf(rowId, tableRef) {
                if (arguments.length != 2) node.warn({err:"[tabUtils][posOf] arguments count mismatch.",count:arguments.length,arg:arguments});
                //look in expected pos first
                if (rowId < tableRef.length && tableRef[rowId].rowId == rowId)
                    return rowId;
                for (let i = 0; i < tableRef.length; i++)
                    if (tableRef[i].rowId == rowId)
                        return i;
                return -1; //not found
            },

            hasRow(rowId, tableRef) {
                return this.posOf(rowId, tableRef) >= 0;
            },

            deleteRowIdx(rowIdx, tableRef){
                if(tableRef.length <= rowIdx || rowIdx < 0)
                    return false;
                for (let i = rowIdx; i < tableRef.length-1; i++)
                    tableRef[i] = tableRef[i + 1];
                tableRef.pop();
                return true;
            },

            deleteRowId(rowId, tableRef) {
                let rowIdx = this.posOf(rowId, tableRef);
                return rowIdx > 0 ? this.deleteRowIdx(rowIdx, tableRef) : false
            },

            deleteRow(row, tableRef){
                return this.deleteRowId(row.rowId, tableRef);
            },

            newRowId(table) {
                //if table empty assign first 
                if(table.length == 0)
                    return 0; //first rowId
                //if rowId equals its table's index -> use shortcut
                if(table[table.length-1].rowId==table.length-1)
                    return table.length;
                //else
                //load all possible indexes
                var indexes = new Array()
                let i = 0;
                for (let row of table)
                    indexes[i++] = true;
                indexes[i++] = true;
                //flag off the used ones
                for (let row of table)
                    indexes[row.rowId] = false;
                //return the first available one
                i = 0;
                while (!indexes[i])
                    i++;
                return i;
