//ensures value is unique in all table.row[fieldKey], appending # plus a number ONLY if necessary
    //returns the (possibly) edited value
    //doesn't modify the table
function madeUnique(value, fieldKey, tab=table){
    //const removeOne = (val, arr) => { arr[arr.indexOf(val)] = arr[arr.length - 1]; arr.pop(); return arr }
    const otherFieldsValue = tab.filter(row => row.rowId!=0).map(row=>row[fieldKey]);
    //try incremental suffixes
    //const similar = tableViewKeys.filter(viewKey => new RegExp("/^"+activeFieldValue+"\s*[0-9]+$/").match(viewKey))
    let i = 0;
    do{
        var newValue = value + (i++==0 ? "" : " #" + (i));
        node.warn({"at":i,"testing":newValue," over ":otherFieldsValue.join(),ov:otherFieldsValue})
    }while(otherFieldsValue.includes(newValue))
    node.warn({txt:fieldKey+" to "+newValue+" to "+value, oth:otherFieldsValue, arg:arguments})
    return newValue;
}


