function styledJoin(arr, pad=undefined){
    return "[ "+arr.map( val => styleAs(typeof(val), (""+val).padEnd(pad) )).join(", ")+" ]";
}

function styledPair(key, val, cls = undefined){
    return styled(key,"key") + ":"+styled(val, cls)
}

function styled(val, cls = typeof(val)){
    return '<span class="' + cls + '">' + val + '</span>'
}

// return formatted HTML version of JSON object
function syntaxHighlight(json) {
    json = global.get("utils").safeStringify(json, 3)
    json = json.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;')
    json = json.replace(/("(\\u[a-zA-Z0-9]{4}|\\[^u]|[^\\"])*"(\s*:)?|\b(true|false|null)\b|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?)/g, function (match) {
        var cls = 'number'
        if ((/^"/).test(match)) {
            if ((/:$/).test(match)) {
                cls = 'key'
            } else {
                cls = 'string'
            }
        } else if ((/true|false/).test(match)) {
            cls = 'boolean'
        } else if ((/null/).test(match)) {
            cls = 'null'
        }
        return '<span class="' + cls + '">' + match + '</span>'
    })
    return json
}

function styleAs(cls, val){
    return '<span class="' + cls + '">' + val + '</span>'
}

