// Code added here will be run once
// whenever the node is started.


class HTMLElement {
    //this should probably be getters and setters that keep isHtmlUpdated updated
    tag
    attributes
    get tagContent(){ return HTMLElement.toTagContent(this.attributes) }
    innerHtml
    html

    isHtmlUpdated
    isLogOn
        //general rule: arguments received are already updated relatively to each other.
    constructor({ tag, attributes, innerHtml, html }){
        const isLogOn = false;
        if(typeof(arguments[0]) == "string")
            (html = arguments[0])       && (!isLogOn || node.warn("setting html to arguments[0]:"+html+" ofType:"+typeof(html)))
        if( !(html || tag)) node.error(new Error("[HTMLElement constructor] either html or tag must be defined. args: " + Object.keys(arguments[0]).map(k => k+":"+arguments[0][k]).join(", ") ))

        //convert attributes var to this class's format
            //accepted:     [{attrName:value},{attrName2:value, attrName3:value}] or {attrName1:value, attrName2:value}
            //converted to: see this.newAttribute()      
        if(attributes){
            let converted = [] 
            //cast 2nd case to 1st
            if(!Array.isArray(attributes)) 
                attributes = [attributes];
            //convert
            attributes.map(group => { isLogOn && node.warn({parsing:group}); Object.keys(group).forEach(key => converted.push(HTMLElement.newAttribute(key, group[key]))) })
            arguments[0].attributes = converted;
            isLogOn && node.warn({"txt":"completed conversion",to:converted, now:attributes, inArgs:typeof(arguments)=="object" ? arguments.attributes : "notInArgs"})
        }

        //init props using arguments or retrieve them from html
        var init;
        ({ tag:this.tag, attributes:this.attributes=[], innerHtml:this.innerHtml="", html:this.html=null } = init = (!html ? arguments[0] : HTMLElement.explodeHtml(html)));
        isLogOn && node.warn({ in: "HTMLElement constructor", txt: "variables set to", varObj: { tag: this.tag, attributes: this.attributes, innerHtml: this.innerHtml, html: this.html }, from: init})    

        //set html using other props if it was not passed among args //assume it's already upToDate otherwise
        if(!this.html)
            this.updateHtml(true)
        this.isHtmlUpdated = true;

        this.isLogOn = true; //falsy: turn off all logging //true: turn on what's explicitly on //>1: turn all on
    }

    static explodeHtml(html, isLogOn=false){
        if(typeof(html) !== "string") return node.error({err:"[HTMLElement explodeHtml] argument is invalid",html:html,args:arguments,e:new TypeError("[HTMLElement explodeHtml] argument is invalid")})
        //explode element // <tag tagContent> innerHtml </tag>
        //var [full, tag, tagContent, innerHtml] = [...((html).matchAll(/<\s*(?<tag>[^\s]+)([^>]+)>((?:(?!<\/\k<tag>>).)*?)<\s*\/\k<tag>\s*>/sg) || [[]])][0]// ?? [console.error("[HTML.. explodeHtml()] failed match for",html)];
        var [full, tag, tagContent, innerHtml] = [...((html).matchAll(/<\s*(?<tag>[^\s]+)((?:.(?<![^=]>))+)>((?:(?!<\/\k<tag>>).)*?)<\s*\/\k<tag>\s*>/sg) || [[]])][0]// ?? [console.error("[HTML.. explodeHtml()] failed match for",html)];
        isLogOn //&& console.log("[full,tag, tagContent, innerHtml] = ",full, ",",tag,",", tagContent, ",", innerHtml);

        //explode attributes from tagContent
            //from                  attrName="value"                |   attrName=""                 |    attrName
            //to [ full ].concat(   [attrName, value, undefined]    |   [attrName, "", undefined]   |   [attrName, undefined, ""]   )
            //mask      [ full,     attrName,   attrValue,      attrValueIfMissing ]
            //of types  [ string,   string,  string|undefined,   undefined|string ]
        var attributes = [];
        for(let regExpRes of tagContent.matchAll(/\s+([^=\s]+)(?:(?:="([^"]*)")|())/g)) {
            var [full, attrName, attrValue, attrValueIfMissing] = regExpRes;    isLogOn //&& console.log("full", full, "name", attrName, " val", attrValue, "ifMiss", attrValueIfMissing);
            attributes.push( { "name": attrName, "value":attrValue ?? "", "isValueMissing": attrValueIfMissing===undefined ? false : true } )
        }

        return { tag:tag, tagContent:tagContent, attributes:attributes, innerHtml:innerHtml, html:html }
    }

    //in any object if attributes is defined it takes precedence over tagContent
    //innerHtml may be an array of explodedHtml 
    static implodeHtml(exploded, isLogOn=false){
        var tagContent = exploded.attributes ? HTMLElement.toTagContent(exploded.attributes) : exploded.toTagContent
        var innerHtml = (Array.isArray(exploded.innerHtml)) ? exploded.innerHtml.map(item => "\n\t"+HTMLElement.implodeHtml(item)).join("\n\t")+"\n\t" : exploded.innerHtml
        return `<${exploded.tag} ${tagContent}>${innerHtml}</${exploded.tag}>`
    }

    hasAttribute(name) { return this.getAttribute() !== undefined }
    getAttribute(name){ // @ts-ignore
        const isLogOn = (false && this.isLogOn) || this.isLogOn > 1;
        true && node.warn({ "retrievingAttr": name, "from": this.attributes, get found(){ return this.attributes.find(attr => attr.name == name)} })
        return this.attributes.find(attr => attr.name == name) ?? undefined;
    }
    getAttributeValue(name){
        let attr = this.getAttribute(name) ?? {};
        return attr.value ?? attr.isValueMissing
    }
    static newAttribute(name, value){
        return { "name":name, "value":""+value ?? "", "isValueMissing": (value === undefined) ? true : false };
    }
    setAttribute(nameOrAttr, value){
        this.isHtmlUpdated = false;
        var attr = typeof(nameOrAttr == "object") ? nameOrAttr : this.getAttribute(nameOrAttr)
        if(!attr){
            this.attributes.push(attr = HTMLElement.newAttribute(nameOrAttr, value))
            return attr;
        }
        else
            return Object.assign(attr, { "value": "" + value ?? "", "isValueMissing": (value === undefined) ? true : false });
    }
    removeAttribute(nameOrAttr){  // @ts-ignore
        const isLogOn = (false && this.isLogOn) || this.isLogOn > 1;
        this.isHtmlUpdated = false;
        let attr = typeof (nameOrAttr == "object") ? nameOrAttr : this.getAttribute(nameOrAttr)
        isLogOn && node.warn({"removing":attr,"param":nameOrAttr,"from":this.attributes})
        let removeIdx = (i,arr) => {arr[i] = arr[arr.length-1]; arr.pop(); return arr}
        if(attr !== undefined)
            removeIdx(this.attributes.findIndex(item => item.name == attr.name), this.attributes)
        return attr !== undefined //was there something to remove ? true : false
    }
    static toTagContent(attributes){
        return attributes.map( attr => attr.name + (attr.isValueMissing ? "" : `="${attr.value}"`)).join(" ")
    }

    get updatedHtml(){ return this.updateHtml()}
    updateHtml(forceUpdate){
        if(forceUpdate ?? this.isHtmlUpdated){
            this.isHtmlUpdated = true;
            this.html = `<${this.tag} ${HTMLElement.toTagContent(this.attributes)}>${this.innerHtml}</${this.tag}>`;
            //attribute object to 'attrName' | 'attrName="attrValue"'
            //let attrToString = attr => attr.name+(attr.isValueMissing ? "" : '="'+attr.value+'"'); //` ${attr.name}${attr.isValueMissing ? "" : `="${attr.value}"`}`;
            //return this.html = `<${this.tag} ${this.attributes.map(attr => attrToString(attr)).join(" ")}>${this.innerHtml}</${this.tag}>`;
        }
        return this.html;
    }

}

